        public static void Delay(int mm)
        {
            DateTime current = DateTime.Now;
            while (current.AddMilliseconds(mm) > DateTime.Now)
            {
                Application.DoEvents();
            }
            return;
        }
        private void button1_Click(object sender, EventArgs e)
        {
            ran();
        }
        public void ran()
        {
            string tmp1 = "";
            int tmp2 = GenerateRandomNumberInRange(0, 2);
            int tmp3 = GenerateRandomNumberInRange(0, 3);
            int tmp4 = GenerateRandomNumberInRange(0, 2);
            if (checkBox1.Checked)
            { tmp1 = tmp1 + nameAdjectives[GenerateRandomNumberInRange(0, nameAdjectives.Length)]; }
            if (tmp2 == 1 && checkBox5.Checked)
            { tmp1 = tmp1 + namePrefices[GenerateRandomNumberInRange(0, namePrefices.Length)]; }
            if (tmp3 == 0 && checkBox3.AutoCheck)
            { tmp1 = tmp1 + nameItems[GenerateRandomNumberInRange(0, nameItems.Length)]; }
            if (tmp3 == 1 && checkBox6.Checked)
            { tmp1 = tmp1 + nameSubjects[GenerateRandomNumberInRange(0, nameSubjects.Length)]; }
            if (tmp3 == 2 && checkBox7.Checked)
            { tmp1 = tmp1 + nameNames[GenerateRandomNumberInRange(0, nameNames.Length)]; }
            if (tmp4 == 0 && checkBox4.Checked)
            { tmp1 = tmp1 + nameVerbs[GenerateRandomNumberInRange(0, nameVerbs.Length)]; }
            if (tmp4 == 1 && checkBox2.Checked)
            { tmp1 = tmp1 + nameConj[GenerateRandomNumberInRange(0, nameConj.Length)]; }
            if (checkBox8.Checked)
            { textBox1.Text = tmp1 + "\r\n" + tmp2 + tmp3 + tmp4; }
            else
            { textBox1.Text = tmp1; }
        }
        private void button2_Click(object sender, EventArgs e)
        {
            for (int a = 0; a <= 1000; a++)
            { ran();
            Delay(10);
            }
        }
